INSERT INTO tb_game_list (name) VALUES ('Aventura e RPG');
INSERT INTO tb_game_list (name) VALUES ('Jogos de plataforma');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Mass Effect Trilogy', 4.8, 2012, 'Role-playing (RPG), Shooter', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/1.png', 'Uma trilogia épica de ficção científica sobre salvar a galáxia.', 'A saga de Comandante Shepard em uma luta épica contra os Reapers, com escolhas que moldam o destino da galáxia.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Red Dead Redemption 2', 4.7, 2018, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/2.png', 'Uma história de sobrevivência no Velho Oeste.', 'Explore o Velho Oeste em uma narrativa profunda e realista sobre lealdade e sobrevivência no final do século XIX.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Witcher 3: Wild Hunt', 4.7, 2014, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/3.png', 'Uma aventura épica de caça a monstros.', 'A jornada de Geralt de Rivia em busca de sua filha adotiva enquanto enfrenta monstros e intrigas políticas.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sekiro: Shadows Die Twice', 3.8, 2019, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/4.png', 'A batalha de um shinobi em busca de vingança.', 'Assuma o papel de um guerreiro shinobi em uma aventura brutal, enfrentando inimigos desafiadores e superando a morte.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ghost of Tsushima', 4.6, 2020, 'Role-playing (RPG), Adventure', 'Playstation', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/5.png', 'Uma luta pela honra e liberdade do Japão feudal.', 'Jin Sakai luta para libertar Tsushima dos invasores mongóis enquanto equilibra honra samurai e táticas furtivas.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Super Mario World', 4.7, 1990, 'Platform', 'Super Ness, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/6.png', 'Clássico dos jogos de plataforma com Mario e Yoshi.', 'Ajude Mario e Yoshi a salvar a Princesa e derrotar Bowser em aventuras por cenários coloridos e desafiantes.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Hollow Knight', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/7.png', 'Exploração sombria em um reino subterrâneo.', 'Embarque em uma jornada desafiadora e atmosférica no reino de Hallownest, cheio de mistérios e segredos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ori and the Blind Forest', 4.0, 2015, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/8.png', 'Uma tocante história de aventura e amizade.', 'Acompanhe Ori em uma belíssima aventura de plataformas, superando desafios e restaurando a paz na floresta.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Cuphead', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/9.png', 'Desafiante jogo inspirado em animações clássicas.', 'Ajude Cuphead e Mugman a pagar sua dívida em um jogo de plataformas cheio de chefões desafiadores.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sonic CD', 4.0, 1993, 'Platform', 'Sega CD, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/10.png', 'Aventura clássica com Sonic e viagens no tempo.', 'Sonic corre contra o tempo para salvar Amy e derrotar Dr. Robotnik em uma aventura cheia de ação e velocidade.');
